import React from "react";
// import "./weather.style.css";

const Weather = props => {
  return (
    <div className="card-container">
      <h1 className="card-city">{props.cityname}</h1>

      <i className={`wi ${props.weatherIcon} display-1 card-icon`} />

      {props.temp_celsius ? (
        <h1 className="card-temperature">{props.temp_celsius}&deg;</h1>
      ) : null}

      <h4 className="card-description">
        {props.description.charAt(0).toUpperCase() + props.description.slice(1)}
      </h4>
    </div>
  );
};

export default Weather;
