import React from "react";
import "./App.css";

import Main from "./components/main";

const API_KEY = "5a2ee489ab444c36a71fb2616b400afb";

// let cityName = "Minsk";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      error: null,
      isLoaded: false
    };
    // this.getWeather();
  }

  // getWeather = async () => {
  //   const api_call = await fetch(
  //     "api.openweathermap.org/data/2.5/weather?q=London&appid=5a2ee489ab444c36a71fb2616b400afb"
  //   );
  //   const response = await api_call.json();
  //   console.log(response);
  // };

  componentDidMount() {
    fetch(
      "api.openweathermap.org/data/2.5/weather?q=London&appid=5a2ee489ab444c36a71fb2616b400afb"
    )
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true
          });
        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  }

  render() {
    return (
      <div className="App">
        <Main />
      </div>
    );
  }
}

export default App;
