import React from "react";

const Form = props => {
  return (
    <div className="form-container">
      <div>{props.error ? error() : null}</div>
      <form onSubmit={props.loadWeather}>
        <input
          type="text"
          className="form-input"
          name="city"
          placeholder="City"
          autoComplete="off"
        />
        <button className="button-weather">Find Weather</button>
      </form>
    </div>
  );
};

function error() {
  return <div>Error</div>;
}

export default Form;
