import React from "react";
// import logo from "../../../weather-app/src/logo.svg";

const Main = props => {
  return (
    <div className="container">
      <h1>Weather App</h1>
      <div className="weather-block">
        <h2>
          {props.city},{/* {props.country} */}
        </h2>
        <div className="weather-icon">
          {/* <img src={logo} alt="logo" /> */}
        </div>

        <h2>{props.temp}&deg;</h2>

        {showTemperature(props.tempMin, props.tempMax)}

        <p className="weather-description">{props.description}</p>
      </div>
    </div>
  );
};

function showTemperature(min, max) {
  return (
    <h3>
      <span>{min}&deg;</span> / <span>{max}&deg;</span>
    </h3>
  );
}

export default Main;
