import React from "react";
import "./App.css";

import Main from "./components/main";

// const API_KEY = "5a2ee489ab444c36a71fb2616b400afb";

// let cityName = "Minsk";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined
    };
    this.getWeather();
  }

  // getWeather = () => {
  //   fetch(
  //     "https://api.openweathermap.org/data/2.5/weather?q=London&appid=5a2ee489ab444c36a71fb2616b400afb"
  //   )
  //     .then(res => res.json())
  //     .then(text => console.log(text));
  // };

  getWeather = async () => {
    const api_call = await fetch(
      "https://api.openweathermap.org/data/2.5/weather?q=London&appid=5a2ee489ab444c36a71fb2616b400afb"
    );
    const data = await api_call.json();
    console.log(data);

    this.setState({
      city: data.name,
      country: data.sys.country,
      temp: data.main.temp,
      tempMin: data.main.temp_min
    });
  };

  render() {
    return (
      <div className="App">
        <Main city={this.state.city} country={this.state.country} />
      </div>
    );
  }
}

export default App;
