import React from "react";
// import "./weather.style.css";

const Weather = props => {
  return (
    <div className="card-container">
      {props.temp_celsius ? (
        <p className="card-temperature">{props.temp_celsius}&deg;</p>
      ) : null}

      <div className="descriptin-block">
        <i className={`wi ${props.weatherIcon} display-1 card-icon`} />

        <div className="deskription-wrapper">
          <p className="card-city">{props.cityname}</p>
          <p className="card-description">
            {props.description.charAt(0).toUpperCase() +
              props.description.slice(1)}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Weather;
