import React from "react";
import "./App.css";
import Form from "./components/form";

import Main from "./components/main";

// const API_KEY = "5a2ee489ab444c36a71fb2616b400afb";

// let cityName = "Minsk";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined,
      // country: undefined,
      // icon: undefined,
      main: undefined,
      convertToCelsius: undefined,
      temp: null,
      tempMax: null,
      tempMin: null,
      description: "",
      error: false
    };
    // this.getWeather();
  }

  convertToCelsius(temp) {
    let cell = Math.floor(temp - 273.15);
    return cell;
  }

  getWeather = async e => {
    e.preventDefault();

    const city = e.target.elements.city.value;

    if (city) {
      const api_call = await fetch(
        "https://api.openweathermap.org/data/2.5/weather?q=?q=${city}&appid=5a2ee489ab444c36a71fb2616b400afb"
      );
      const data = await api_call.json();
      console.log(data);

      this.setState({
        city: data.name,
        // country: data.sys.country,
        temp: this.convertToCelsius(data.main.temp),
        tempMin: this.convertToCelsius(data.main.temp_min),
        tempMax: this.convertToCelsius(data.main.temp_max),
        description: data.weather[0].description,
        error: false
      });
    } else {
      this.setState({
        error: true
      });
    }
  };

  render() {
    return (
      <div className="App">
        <Form loadWeather={this.getWeather} error={this.state.error} />

        <Main
          city={this.state.city}
          country={this.state.country}
          temp={this.state.temp}
          tempMin={this.state.tempMin}
          tempMax={this.state.tempMax}
          description={this.state.description}
        />
      </div>
    );
  }
}

export default App;
